function botaoAtivo(){var e=document.querySelectorAll("[data-tab-button]");for(let t=0;t<e.length;t++)e[t].classList.remove("shows__tabs__button--is-active")}function escondeAbas(){var e=document.querySelectorAll("[data-tab-id]");for(let t=0;t<e.length;t++)e[t].classList.remove("shows__list--is-active")}document.addEventListener("DOMContentLoaded",function(){var e=document.querySelectorAll("[data-tab-button]");for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(t){var e=t.target.dataset.tabButton,e=document.querySelector(`[data-tab-id=${e}]`);escondeAbas(),botaoAtivo(),t.target.classList.add("shows__tabs__button--is-active"),e.classList.add("shows__list--is-active")})});